// src/pages/QuestoesPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';

export default function QuestoesPage() {
  const { id } = useParams();
  const [questoes, setQuestoes] = useState([]);
  const [erro, setErro] = useState('');

  useEffect(() => {
    api.get(`Materia/${id}/questoes`)
      .then((res) => setQuestoes(res.data))
      .catch(() => setErro('Erro ao buscar questões. Tente novamente.'));
  }, [id]);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800 text-center">Questões</h1>
      {erro && <p className="text-red-500 mb-4 text-center">{erro}</p>}
      <div className="space-y-4 max-w-3xl mx-auto">
        {questoes.map((q) => (
          <div key={q.id} className="bg-white p-5 rounded-xl shadow hover:shadow-lg transition">
            <p className="text-gray-700 font-medium">{q.enunciado}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
