import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import './StudyPage.css';

export default function StudyPage() {
  const navigate = useNavigate();
  const [materias, setMaterias] = useState([]);
  const [selectedMaterias, setSelectedMaterias] = useState([]);
  const [numQuestoesPorMateria, setNumQuestoesPorMateria] = useState({});
  const [tempo, setTempo] = useState(30);
  const [tempoInfinito, setTempoInfinito] = useState(false);
  const [erro, setErro] = useState('');

  useEffect(() => {
    const fetchMaterias = async () => {
      try {
        const response = await api.get('Materia');
        setMaterias(response.data);
      } catch {
        setErro('Erro ao buscar matérias. Faça login novamente.');
      }
    };
    fetchMaterias();
  }, []);

  const handleCheckbox = (id) => {
    if (selectedMaterias.includes(id)) {
      setSelectedMaterias(selectedMaterias.filter((mid) => mid !== id));
      const newNums = { ...numQuestoesPorMateria };
      delete newNums[id];
      setNumQuestoesPorMateria(newNums);
    } else {
      setSelectedMaterias([...selectedMaterias, id]);
      setNumQuestoesPorMateria({ ...numQuestoesPorMateria, [id]: 10 });
    }
  };

  const handleNumQuestoesChange = (id, value) => {
    const num = Math.max(1, Math.min(50, Number(value))); // limite entre 1 e 50
    setNumQuestoesPorMateria({ ...numQuestoesPorMateria, [id]: num });
  };

  const handleStart = () => {
    if (selectedMaterias.length === 0) {
      setErro('Selecione pelo menos uma matéria.');
      return;
    }

    navigate(`/materias/${selectedMaterias.join(',')}/questoes`, {
      state: { numQuestoesPorMateria, tempo: tempoInfinito ? null : tempo },
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="study-page">
      <div className="study-card">
        <h1>Preparar Sessão de Estudo</h1>
        {erro && <p className="error">{erro}</p>}

        {/* =================== Seção de Matérias =================== */}
        <div className="section">
          <h2>Matérias</h2>
          <p>Selecione uma ou mais matérias e defina a quantidade de questões para cada uma (1 a 50).</p>
          {materias.map((m) => (
            <div key={m.id} className="checkbox-group">
              <input
                type="checkbox"
                id={`materia-${m.id}`}
                checked={selectedMaterias.includes(m.id)}
                onChange={() => handleCheckbox(m.id)}
              />
              <label htmlFor={`materia-${m.id}`} className="materia-label">
                {m.nome}
              </label>

              {selectedMaterias.includes(m.id) && (
                <div className="num-questoes-wrapper">
                  <label htmlFor={`num-questoes-${m.id}`} className="num-questoes-label">
                    Quantidade de questões para esta matéria:
                  </label>
                  <input
                    id={`num-questoes-${m.id}`}
                    type="number"
                    min="1"
                    max="50"
                    value={numQuestoesPorMateria[m.id] || 10}
                    onChange={(e) => handleNumQuestoesChange(m.id, e.target.value)}
                    className="input-field small-input"
                    disabled={tempoInfinito}
                  />
                </div>
              )}
            </div>
          ))}
        </div>

        {/* =================== Seção de Tempo =================== */}
        <div className="section">
          <h2>Tempo de estudo</h2>
          <div className="form-group">
            <label>
              <input
                type="checkbox"
                checked={tempoInfinito}
                onChange={(e) => setTempoInfinito(e.target.checked)}
              />{' '}
              Tempo ilimitado
            </label>
          </div>

          {!tempoInfinito && (
            <div className="form-group">
              <label>Tempo (minutos, 1 a 300):</label>
              <input
                type="number"
                min="1"
                max="300"
                value={tempo}
                onChange={(e) => setTempo(Math.max(1, Math.min(300, Number(e.target.value))))}
                className="input-field"
              />
            </div>
          )}
        </div>

        <button className="btn-submit" onClick={handleStart}>
          Começar
        </button>

        <button className="btn-logout" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}
