// src/pages/StudyPage.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import './StudyPage.css';

export default function StudyPage() {
  const navigate = useNavigate();
  const [materias, setMaterias] = useState([]);
  const [selectedMateria, setSelectedMateria] = useState('');
  const [numQuestoes, setNumQuestoes] = useState(10);
  const [tempo, setTempo] = useState(30); // tempo em minutos
  const [erro, setErro] = useState('');

  // Buscar matérias da API
  useEffect(() => {
    const fetchMaterias = async () => {
      try {
        const response = await api.get('Materia'); // ajusta o endpoint se necessário
        setMaterias(response.data);
      } catch {
        setErro('Erro ao buscar matérias. Faça login novamente.');
      }
    };
    fetchMaterias();
  }, []);

  const handleStart = () => {
    if (!selectedMateria) {
      setErro('Selecione uma matéria.');
      return;
    }
    // redireciona para a página de questões da matéria, passando parâmetros
    navigate(`/materias/${selectedMateria}/questoes`, {
      state: { numQuestoes, tempo },
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="study-page">
      <div className="study-card">
        <h1>Preparar Sessão de Estudo</h1>
        {erro && <p className="error">{erro}</p>}

        <div className="form-group">
          <label>Matéria:</label>
          <select
            className="input-field"
            value={selectedMateria}
            onChange={(e) => setSelectedMateria(e.target.value)}
          >
            <option value="">Selecione a matéria</option>
            {materias.map((m) => (
              <option key={m.id} value={m.id}>
                {m.nome}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Quantas questões:</label>
          <input
            className="input-field"
            type="number"
            min="1"
            max="100"
            value={numQuestoes}
            onChange={(e) => setNumQuestoes(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Tempo (minutos):</label>
          <input
            className="input-field"
            type="number"
            min="5"
            max="180"
            value={tempo}
            onChange={(e) => setTempo(e.target.value)}
          />
        </div>

        <button className="btn-submit" onClick={handleStart}>
          Começar
        </button>

        <button className="btn-logout" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}
