// src/pages/MateriasPage.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';

export default function MateriasPage() {
  const [materias, setMaterias] = useState([]);
  const [erro, setErro] = useState('');

  useEffect(() => {
    api.get('Materia')
      .then((res) => setMaterias(res.data))
      .catch(() => setErro('Erro ao buscar matérias. Faça login novamente.'));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800 text-center">Matérias</h1>
      {erro && <p className="text-red-500 mb-4 text-center">{erro}</p>}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {materias.map((m) => (
          <Link
            key={m.id}
            to={`/materias/${m.id}/questoes`}
            className="bg-white p-5 rounded-xl shadow hover:shadow-lg transition text-center font-semibold text-gray-700"
          >
            {m.nome}
          </Link>
        ))}
      </div>
    </div>
  );
}
