import React, { useState } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const [cadastro, setCadastro] = useState(false);
  const [nome, setNome] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
  e.preventDefault();
  setErro('');
  try {
    const response = await api.post('Usuario/login', { email, senha });
    const { token } = response.data;
    localStorage.setItem('token', token); // Salva o token
    navigate('/dashboard');
  } catch (err) {
    setErro('Login falhou. Verifique seus dados.');
  }
};

  const handleCadastro = async (e) => {
    e.preventDefault();
    setErro('');
    try {
      await api.post('Usuario/cadastro', { nome, email, senha });
      setCadastro(false);
      setNome('');
      setEmail('');
      setSenha('');
      alert('Cadastro realizado com sucesso! Faça login.');
    } catch (err) {
      setErro('Cadastro falhou. Tente outro email.');
    }
  };

  return (
    <div>
      <h1>{cadastro ? 'Cadastro' : 'Login'}</h1>
      {cadastro ? (
        <form onSubmit={handleCadastro}>
          <input
            type="text"
            placeholder="Nome"
            value={nome}
            onChange={e => setNome(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Senha"
            value={senha}
            onChange={e => setSenha(e.target.value)}
            required
          />
          <button type="submit">Cadastrar</button>
          <button type="button" onClick={() => setCadastro(false)}>Já tenho conta</button>
        </form>
      ) : (
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Senha"
            value={senha}
            onChange={e => setSenha(e.target.value)}
            required
          />
          <button type="submit">Entrar</button>
          <button type="button" onClick={() => setCadastro(true)}>Cadastrar</button>
        </form>
      )}
      {erro && <p style={{ color: 'red' }}>{erro}</p>}
    </div>
  );
}

export default LoginPage;
