// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const [cadastro, setCadastro] = useState(false);
  const [nome, setNome] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setErro('');
    try {
      const response = await api.post('Usuario/login', { email, senha });
      const { token } = response.data;
      localStorage.setItem('token', token);
      navigate('/dashboard');
    } catch {
      setErro('Login falhou. Verifique seus dados.');
    }
  };

  const handleCadastro = async (e) => {
    e.preventDefault();
    setErro('');
    try {
      await api.post('Usuario/cadastro', { nome, email, senha });
      setCadastro(false);
      setNome('');
      setEmail('');
      setSenha('');
      alert('Cadastro realizado com sucesso! Faça login.');
    } catch {
      setErro('Cadastro falhou. Tente outro email.');
    }
  };

  return (
  <div className="container">
    <h1>{cadastro ? 'Cadastro' : 'Login'}</h1>

    {erro && <p style={{ color: 'red' }}>{erro}</p>}

    <form onSubmit={cadastro ? handleCadastro : handleLogin}>
      {cadastro && (
        <input
          className="input"
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
      )}
      <input
        className="input"
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        className="input"
        type="password"
        placeholder="Senha"
        value={senha}
        onChange={(e) => setSenha(e.target.value)}
        required
      />
      <button className="button" type="submit">
        {cadastro ? 'Cadastrar' : 'Entrar'}
      </button>
    </form>
  </div>
);
}
