import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';

function QuestoesPage() {
  const { id } = useParams(); // Obtém o ID da matéria da URL
  const [questoes, setQuestoes] = useState([]);
  const [erro, setErro] = useState('');

  useEffect(() => {
    const fetchQuestoes = async () => {
      try {
        const response = await api.get(`Materia/${id}/questoes`);
        setQuestoes(response.data);
      } catch (err) {
        setErro('Erro ao buscar questões. Tente novamente.');
      }
    };
    fetchQuestoes();
  }, [id]);

  return (
    <div>
      <h1>Questões</h1>
      {erro && <p style={{ color: 'red' }}>{erro}</p>}
      <ul>
        {questoes.map(q => (
          <li key={q.id}>
            <strong>Enunciado:</strong> {q.enunciado}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default QuestoesPage;
