// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const [cadastro, setCadastro] = useState(false);
  const [nome, setNome] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setErro('');
    try {
      const response = await api.post('Usuario/login', { email, senha });
      const { token } = response.data;
      localStorage.setItem('token', token);
      navigate('/dashboard');
    } catch {
      setErro('Login falhou. Verifique seus dados.');
    }
  };

  const handleCadastro = async (e) => {
    e.preventDefault();
    setErro('');
    try {
      await api.post('Usuario/cadastro', { nome, email, senha });
      setCadastro(false);
      setNome('');
      setEmail('');
      setSenha('');
      alert('Cadastro realizado com sucesso! Faça login.');
    } catch {
      setErro('Cadastro falhou. Tente outro email.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 to-indigo-500">
      <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center text-gray-800">
          {cadastro ? 'Cadastro' : 'Login'}
        </h1>

        {erro && <p className="text-red-500 mb-4">{erro}</p>}

        <form onSubmit={cadastro ? handleCadastro : handleLogin} className="space-y-4">
          {cadastro && (
            <input
              type="text"
              placeholder="Nome"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
              required
            />
          )}
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
            required
          />
          <input
            type="password"
            placeholder="Senha"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
            required
          />

          <button
            type="submit"
            className="w-full bg-indigo-500 hover:bg-indigo-600 text-white py-3 rounded-lg font-semibold transition"
          >
            {cadastro ? 'Cadastrar' : 'Entrar'}
          </button>
        </form>

        <div className="mt-4 text-center">
          <button
            className="text-indigo-500 hover:underline"
            onClick={() => setCadastro(!cadastro)}
          >
            {cadastro ? 'Já tenho conta' : 'Cadastrar'}
          </button>
        </div>
      </div>
    </div>
  );
}
